import React, { useState } from "react";
import "./RecipeList.css";
import Navbar from "./Navbar";
import robotImage from "./images/robot-image.png";

function RecipeList() {
  const [ingredients, setIngredients] = useState("");
  const [generatedRecipe, setGeneratedRecipe] = useState("");
  const [loading, setLoading] = useState(false);

  const generateRecipes = async () => {
    try {
      setLoading(true);
      const response = await fetch(
        `http://localhost:3001/generate-recipe?ingredients=${encodeURIComponent(ingredients)}`
      );

      const recipeText = await response.text();
      console.log("Generated recipe:", recipeText);

      setGeneratedRecipe(recipeText);
    } catch (error) {
      console.error("Error generating recipe:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generateRecipes();
  };

  return (
    <>
      <Navbar />
      <div className="App">
        <div className="left-column">
          <img src={robotImage} alt="Robot" className="robot-image" />
        </div>
        <div className="right-column">
          <form onSubmit={handleSubmit}>
            <textarea
              value={ingredients}
              onChange={(e) => setIngredients(e.target.value)}
              placeholder="Enter the ingredients you have one per line"
              rows={5}
              cols={50}
            />
            <br />
            <button type="submit" className="generate-button">
              {loading
                ? "Generating..."
                : generatedRecipe
                ? "Regenerate"
                : "Generate"}
            </button>
          </form>
          <div className="recipe-container">
            {generatedRecipe && (
              <div className="generated-recipe">
                <pre
                  dangerouslySetInnerHTML={{
                    __html: generatedRecipe.replace(
                      /(Recipe Name:\n|Ingredients:\n|Instructions:\n)/g,
                      '<span class="header">$1</span>'
                    ),
                  }}
                ></pre>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default RecipeList;
