import React, { useState } from "react";
import ScrollTrigger from "react-scroll-trigger";
import "./RecipeList.css";

function App() {
  // API Key: 808c804f9a254e5cbafaccc95464ce05
  const [ingredients, setIngredients] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [visibleRecipes, setVisibleRecipes] = useState([]);

  const fetchRecipes = async () => {
    const apiUrl = `https://api.spoonacular.com/recipes/findByIngredients?ingredients=${encodeURIComponent(
      ingredients
    )}&number=5&apiKey=808c804f9a254e5cbafaccc95464ce05`;

    try {
      const response = await fetch(apiUrl);
      if (!response.ok) {
        throw new Error(`HTTP error ${response.status}`);
      }
      const data = await response.json();
      console.log("Recipes data:", data);

      // Fetch recipe details for all recipes
      const recipeDetailsPromises = data.map((recipe) =>
        fetchRecipeDetails(recipe.id)
      );
      const recipeDetails = await Promise.all(recipeDetailsPromises);

      setRecipes(recipeDetails);
    } catch (error) {
      console.error("Error fetching recipes:", error);
    }
  };

  const onEnterViewport = (recipeId) => {
    setVisibleRecipes((prevVisibleRecipes) => {
      if (!prevVisibleRecipes.includes(recipeId)) {
        return [...prevVisibleRecipes, recipeId];
      }
      return prevVisibleRecipes;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchRecipes();
  };

  const fetchRecipeDetails = async (recipeId) => {
    const response = await fetch(
      `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=808c804f9a254e5cbafaccc95464ce05`
    );
    const data = await response.json();

    // Extract ingredients and instructions from the response
    const ingredients = data.extendedIngredients.map(
      (ingredient) => ingredient.original
    );
    const instructions =
      data.analyzedInstructions[0]?.steps.map((step) => step.step) || [];

    // Return the recipe object with the ingredients and instructions
    return { ...data, ingredients, instructions };
  };

  return (
    <div className="App">
      <h1>AI Chef</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
          placeholder="Enter the ingredients you have one per line"
          rows={5}
          cols={50}
        />
        <br />
        <button type="submit">Generate</button>
      </form>
      <div className="recipe-container">
        {recipes &&
          recipes.map((recipe) => (
            <ScrollTrigger
              key={recipe.id}
              onEnter={() => onEnterViewport(recipe.id)}
              triggerOnLoad={false}
            >
              <div
                className={`recipe ${
                  visibleRecipes.includes(recipe.id) ? "visible" : ""
                }`}
                onClick={() => fetchRecipeDetails(recipe.id)}
              >
                <div className="image-container">
                  <img src={recipe.image} alt={recipe.title} />
                </div>
                <div className="instructions">
                  <h2>{recipe.title}</h2>
                  <h3>Ingredients</h3>
                  <ul>
                    {recipe.ingredients &&
                      recipe.ingredients.map((ingredient) => (
                        <li key={ingredient.id}>{ingredient}</li>
                      ))}
                  </ul>
                  <h3>Instructions</h3>
                  <ol>
                    {recipe.instructions &&
                      recipe.instructions.map((instruction) => (
                        <li key={instruction.id}>{instruction}</li>
                      ))}
                  </ol>
                </div>
              </div>
            </ScrollTrigger>
          ))}
      </div>
    </div>
  );
}
