import React, { useState } from "react";
import "./RecipeList.css";

function App() {
  // API Key: 808c804f9a254e5cbafaccc95464ce05
  const [ingredients, setIngredients] = useState("");
  const [recipes, setRecipes] = useState([]);

  const fetchRecipes = async () => {
    const ingredientsList = ingredients.split("\n").join(",");
    const apiUrl = `https://api.spoonacular.com/recipes/findByIngredients?apiKey=808c804f9a254e5cbafaccc95464ce05&ingredients=${ingredientsList}`;
    const response = await fetch(apiUrl);
    const data = await response.json();
    console.log("API Response:", data);
    setRecipes(data);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchRecipes();
  };

  const fetchRecipeDetails = async (recipeId) => {
    const response = await fetch(
      `https://api.spoonacular.com/recipes/${recipeId}/information?apiKey=808c804f9a254e5cbafaccc95464ce05`
    );
    const data = await response.json();

    // Extract ingredients and instructions from the response
    const ingredients = data.extendedIngredients.map(
      (ingredient) => ingredient.original
    );
    const instructions =
      data.analyzedInstructions[0]?.steps.map((step) => step.step) || [];

    // Update the recipe in the state
    setRecipes((prevRecipes) =>
      prevRecipes.map((recipe) =>
        recipe.id === recipeId
          ? { ...recipe, ingredients, instructions }
          : recipe
      )
    );
  };

  return (
    <div className="App">
      <h1>AI Chef</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
          placeholder="Enter ingredients, one per line"
          rows={5}
          cols={50}
        />
        <br />
        <button type="submit">Generate</button>
      </form>
      <div className="recipe-container">
        {recipes &&
          recipes.map((recipe) => (
            <div className="recipe" key={recipe.id}>
              <div className="image-container">
                <img src={recipe.image} alt={recipe.title} />
              </div>
              <div className="instructions">
                <h2>{recipe.title}</h2>
                <h3>Ingredients</h3>
                <ul>
                  {recipe.ingredients &&
                    recipe.ingredients.map((ingredient) => (
                      <li key={ingredient.id}>{ingredient}</li>
                    ))}
                </ul>
                <h3>Instructions</h3>
                <ol>
                  {recipe.instructions &&
                    recipe.instructions.map((instruction) => (
                      <li key={instruction.id}>{instruction}</li>
                    ))}
                </ol>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default App;
