const generateRecipes = async () => {
  try {
    setLoading(true);
    const response = await fetch(
      `http://localhost:3001/generate-recipe?ingredients=${encodeURIComponent(ingredients)}`
    );

    const recipeData = await response.json(); // receive JSON response
    console.log("Received recipe data:", recipeData);

    // Create recipe object using data from the API
    const recipe = {
      name: recipeData.name,
      ingredients: recipeData.ingredients.split('\n'),
      instructions: recipeData.instructions
    };

    setGeneratedRecipe(recipe);
  } catch (error) {
    console.error("Error generating recipe:", error);
  } finally {
    setLoading(false);
  }
};
