import React, { useState } from "react";
import "./RecipeList.css";

function RecipeList() {
  const [ingredients, setIngredients] = useState("");
  const [generatedRecipe, setGeneratedRecipe] = useState("");

  const generateRecipes = async () => {
    try {
      const prompt = `Create a recipe using the following ingredients:\n${ingredients}\n\nRecipe:\n`;

      const response = await fetch("https://api.openai.com/v1/engines/davinci-codex/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer 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`, // Replace this with your API key
        },
        body: JSON.stringify({
          prompt: prompt,
          max_tokens: 150,
          n: 1,
          stop: null,
          temperature: 0.8,
        }),
      });

      const data = await response.json();
      const recipeText = data.choices[0].text.trim();
      console.log("Generated recipe:", recipeText);

      setGeneratedRecipe(recipeText);
    } catch (error) {
      console.error("Error generating recipe:", error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generateRecipes();
  };

  return (
    <div className="App">
      <h1>AI Chef</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
          placeholder="Enter the ingredients you have one per line"
          rows={5}
          cols={50}
        />
        <br />
        <button type="submit" className="generate-button">
          {generatedRecipe ? "Regenerate" : "Generate"}
        </button>
      </form>
      <div className="recipe-container">
        {generatedRecipe && (
          <div className="generated-recipe">
            <pre>{generatedRecipe}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default RecipeList;
