import React, { useState } from "react";
import "./RecipeList.css";
import Navbar from "./Navbar";
import robotImage from "./images/robot-image.png";

function RecipeList() {
  const [ingredients, setIngredients] = useState("");
  const [generatedRecipe, setGeneratedRecipe] = useState(null);
  const [loading, setLoading] = useState(false);

  const generateRecipes = async () => {
    try {
      setLoading(true);
      const response = await fetch(
        `http://localhost:3001/generate-recipe?ingredients=${encodeURIComponent(
          ingredients
        )}`
      );

      const recipeText = await response.text();
      console.log("Generated recipe:", recipeText);

      const [name, ...rest] = recipeText.split("\n\n");
      const [ingredients, instructions] = rest.join("\n\n").split("\n\n");

      const recipe = {
        name: name.replace("Recipe Name:\n", ""),
        ingredients: ingredients
          .replace("Ingredients:\n", "")
          .split("\n")
          .map((i) => i.replace("- ", "")),
        instructions: instructions.replace("Instructions:\n", ""),
      };

      setGeneratedRecipe(recipe);
    } catch (error) {
      console.error("Error generating recipe:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generateRecipes();
  };

  return (
    <>
      <Navbar />
      <div className="App">
        <div className="content-container">
          {generatedRecipe ? (
            <div className="generated-recipe">
              <h2>Recipe Name: {generatedRecipe.name}</h2>
              <h3>Ingredients:</h3>
              <ul>
                {generatedRecipe.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))}
              </ul>
              <h3>Instructions:</h3>
              <p>{generatedRecipe.instructions}</p>
            </div>
          ) : (
            <img src={robotImage} alt="Robot Chef" className="robot-image" />
          )}
          <div className="form-container">
            <h1>AI Chef</h1>
            <form onSubmit={handleSubmit}>
              <textarea
                value={ingredients}
                onChange={(e) => setIngredients(e.target.value)}
                placeholder="Enter the ingredients you have one per line"
                rows={5}
                cols={50}
              />
              <br />
              <button type="submit" className="generate-button">
                {loading
                  ? "Generating..."
                  : generatedRecipe
                  ? "Regenerate"
                  : "Generate"}
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default RecipeList;
